var roboexploreApp = {
	App: angular.module('roboexploreApp', ['ui.router', 'roboexploreDirectives', 'roboexploreControllers']),
	Directives: angular.module('roboexploreDirectives', []),
	Controllers: angular.module('roboexploreControllers', [])
};

roboexploreApp.App.config(uirouteConfig);
window.roboexploreApp = roboexploreApp;

function uirouteConfig($stateProvider, $urlRouterProvider){
	$urlRouterProvider.otherwise("/")
	$stateProvider
		.state('default', {
			url: '/',
			templateUrl: 'features/world/world-partial.html'
		});
}

'use strict';
roboexploreApp.Controllers
    .controller('worldCtrl', [
        '$scope',
        function worldCtrl($scope) {
            $scope.tileRows = [];
			for(var i = 0; i < 20; i++){
				$scope.tileRows[i] = [];
				for(var j = 0; j < 20; j++){
					if(i >=9 && i <= 11 && j <=2){
						$scope.tileRows[i][j] = {elevation: -1};
					}else{
						$scope.tileRows[i][j] = {elevation: parseInt(i / 3 + j / 3)};
					}
				}
			}
        }
    ]);

'use strict'

roboexploreApp
    .Directives
    .directive('tile',
        function tileDirective() {
            return {
                restrict: 'E',
                scope: {
					elevation: '@'
				},
                templateUrl: 'features/tile/tile-partial.html'
            };
        }
	);

'use strict'

roboexploreApp
    .Directives
    .directive('robot',
        function robotDirective() {
			function setPosition(scope, element, attrs){
				scope.updatePosition(0, 0);
			}
			
            return {
                restrict: 'E',
                templateUrl: 'features/robot/robot-partial.html',
                controller: 'robotCtrl',
				link: setPosition
            };
        }
	);

'use strict';
roboexploreApp.Controllers
    .controller('robotCtrl', [
        '$scope',
        function robotCtrl($scope) {
            $scope.updatePosition = function updatePosition(newTop, newLeft){
				$scope.top = newTop;
				$scope.topPixels = newTop * 20 + 'px';
				$scope.left = newLeft;
				$scope.leftPixels = newLeft * 20 + 'px';
			}
			
			$scope.moveRight = function moveRight(rightDelta){
				$scope.updatePosition($scope.top, $scope.left + parseInt(rightDelta));
			}

			$scope.moveDown = function moveDown(downDelta){
				$scope.updatePosition($scope.top + parseInt(downDelta), $scope.left);
			}
		}
    ]);
